      // }

      render() {
            return (
                  <div style = { userStyle } >
                       {this.state.users.map( (user) => (
                              <UserItem key  = { user.id} 
                                        user = { user } />
                       ))} 
                  </div>
            )
      }
}

const userStyle = {
      display : 'grid',
      gridGap : '1rem',
      gridTemplateColumns :'repeat(3, 1fr)'
};

export default Users
