      };

      render() {

            const { 
                  bio,
                  blog,
                  name,
                  login,
                  html_url,
                  hireable,
                  location,
                  followers,
                  following,
                  avatar_url,
                  public_repos,
                  public_gists

                  } = this.props.user;

            const { loading } = this.props;


            return (
                  <Fragment>
                      <Link to = '/' className = 'btn btn-light' >Back to Search</Link>

                      Hireable: {' '}
                      { hireable ? <span className = 'text-success'>YES</span>  
                                 : <span className = 'text-danger' >NOT</span> }



                  </Fragment>
            )
      }
}

export default User
