import React, { Component } from 'react'
import Spinner from '../layout/Spinner'

class User extends Component {

      componentDidMount () {
            this.props.getUser(this.props.match.params.login);
      };

      render() {

            const { 
                  bio,
                  blog,
                  name,
                  login,
                  html_url,
                  hireable,
                  location,
                  followers,
                  following,
                  avatar_url,
                  public_repos,
                  public_gists

                  } = this.props.user;

            const { loading } = this.props;
            alert(this.state.loading)
            if (loading) {
                 return <Spinner/>
            } else {
                  return (
                  <div>
                      {name}
                  </div>
            ) 
            }


      }
}

export default User
